def getTokenAmountForBuy(buying_token, token, amount):
    pair = getPairAddress(buying_token,token)
    reserve = getReserves(pair)
    position = getTokensPositionInPool(pair, buying_token)
    if position == 0:
        return int((web3.toWei(amount,'ether') / reserve[0]) * reserve[1])
    else:
        return int((web3.toWei(amount,'ether') / reserve[1]) * reserve[0])


def getPairAddress(address_1,address_2):
    address_1 = Web3.toChecksumAddress(address_1)
    address_2 = Web3.toChecksumAddress(address_2)
    pair_address = main_contract.functions.getPair(address_1,address_2).call()
    return pair_address


def getTokensPositionInPool(pair, token):
    pair = web3.eth.contract(address=pair, abi=lp_standart_abi)
    if pair.functions.token0().call() == token:
        return 0
    else:
        return 1


def getReserves(pair_address):
	pair = web3.eth.contract(address=pair_address, abi=lp_standart_abi)
	pool = pair.functions.getReserves().call()
	return pool
  

def balanceOf(person,token):
    erc20 = web3.eth.contract(address=token, abi=safemoon_abi)
    balanceOf = erc20.functions.balanceOf(person).call()
    return balanceOf


def getBalance(person):
    balance = web3.eth.get_balance(person)
    return web3.fromWei(balance, 'ether') 


def checkTX(txid, delay):
    while True:
        try:
            responce = web3.eth.get_transaction_receipt(txid)['status']
        except:
            print('Wait for responce...')
        else:
            return responce
        finally:
            time.sleep(delay)


